import React, {useState} from "react";
import platformService from "../../services/PlatformService";
import contentService from "../../services/PlatformService";

const addPlatformModal = ({ isOpen, onClose }) => {
    const [contentLink, setContentLink] = useState('');
    const [error, setError] = useState('');

    // To detect platform and content type from URL
    const detectPlatformInfo = (url) => {
        // Platform detection patterns
        if (url.includes('youtube.com') || url.includes('youtu.be')) {
            // YouTube detection
            let videoId = '';

            if (url.includes('watch?v=')){
                // Regular video
                videoId = url.split('watch?v=')[1].split('&')[0];
            } else if (url.includes('youtu.be/')) {
                // Short
                videoId = url.split('youtu.be/')[1].split('?')[0];
            } else if (url.includes('shorts/')) {
                // Shorts
                videoId = url.split('shorts/')[1].split('?')[0];
            }

            return {
                platform: 'YouTube',
                contentId: videoId,
                contentType: url.includes('shorts/') ? 'SHORT' : 'VIDEO'
            };
        } else if (url.includes('instagram.com')) {
            // Ig detection
            let contentId = '';
            let contentType = 'POST';

            if (url.includes('/p/')) {
                // Regular post
                contentId = url.split('/p/')[1].split('/')[0];
            } else if (url.includes('/reel/')) {
                // Reel
                contentId = url.split('/reel/')[1].split('/')[0];
                contentType = 'REEL';
            } else if (url.includes('/stories/')) {
                // Story
                contentId = url.split('/stories/')[1].split('/')[1];
                contentType = 'STORY';
            }

            return {
                platform: 'Instagram',
                contentId: contentId,
                contentType: contentType
            };
        } else if (url.includes('tiktok.com')) {
            // TikTok detection
            let contentId = '';

            if (url.includes('/video/')) {
                contentId = url.split('/video/')[1].split('?')[0];
            }

            return {
                platform: 'TikTok',
                contentId: contentId,
                contentType: 'VIDEO'
            };
        }

        // Default return for unrecognized
        return {
            platform: 'Other',
            contentId: '',
            contentType: 'UNKNOWN'
        };
    };

    // Handle content addition
    const handleAddContent = async () => {
        // Validating URL
        if (!contentLink.trim()) {
            setError('Please enter a valid content link');
            return;
        }

        try {
            // Detect platform and content info
            const contentInfo = detectPlatformInfo(contentLink);

            if (contentInfo.contentId === '') {
                setError('Could not detect cont ID from the provided link');
                return;
            }

            // Call service to add content
            await contentService.addContent({
                url: contentLink,
                platform: contentInfo.platform,
                contentId: contentInfo.contentId,
                contentType: contentInfo.contentType
            });

            // Reset and close modal
            setContentLink('');
            setError('');
            onClose();
        } catch (err) {
            setError('Failed to add content. Please try again..')
        }
    };
    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-xl w-96">
                <h2 className="text-xl font-bold mb-4">Add New Content</h2>

                <input
                    type="text"
                    value={contentLink}
                    onChange={(e) => setContentLink(e.target.value)}
                    placeholder="Paste video/reel/post link here"
                    className="w-full p-2 border rounded mb-4"
                />

                {error && (
                    <p className="text-red-500 mb-4">{error}</p>
                )}

                <div className="flex justify-end space-x-2">
                    <button
                        onClick={onClose}
                        className="px-4 py-2 bg-gray-200 rounded"
                    >
                        Cancel
                    </button>
                    <button
                        onClick={handleAddContent}
                        className="px-4 py-2 bg-blue-500 text-white rounded"
                    >
                        Add Content
                    </button>
                </div>
            </div>
        </div>
    );
};

export default AddPlatformModal;
