import React, { useState } from 'react';
import {Link, useNavigate} from 'react-router-dom';
import {
    LayoutDashboard,
    Users,
    Home,
    PlusCircle,
    LogOut
} from 'lucide-react';
import AddPlatformModal from '../Platforms/AddPlatformModal';
import {useAuth} from "../../context/AuthContext";

const Sidebar = () => {
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);
    const { logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => async () => {
        await logout();
        navigate('/login');
    }

    return (
        <div className="fixed left-0 top-0 h-full w-64 bg-gray-800 text-white p-4 flex flex-col">
            {/* Logo or App Name */}
            <div className="text-2xl font-bold mb-10 text-center">
                Content Analytics
            </div>

            {/* Navigation Links */}
            <nav className="flex-grow">
                <ul className="space-y-2">
                    <li>
                        <Link
                            to="/"
                            className="flex items-center p-2 hover:bg-gray-700 rounded transition"
                        >
                            <Home className="mr-3" />
                            Home
                        </Link>
                    </li>
                    <li>
                        <Link
                            to="/dashboard"
                            className="flex items-center p-2 hover:bg-gray-700 rounded transition"
                        >
                            <LayoutDashboard className="mr-3" />
                            Dashboard
                        </Link>
                    </li>
                    <li>
                        <Link
                            to="/platforms"
                            className="flex items-center p-2 hover:bg-gray-700 rounded transition"
                        >
                            <Users className="mr-3" />
                            Platforms
                        </Link>
                    </li>
                </ul>
            </nav>

            {/* Logout Button */}
            <div
                onClick={handleLogout}
                className="mb-6 mt-auto mx-auto bg-red-500 hover:bg-red-600 rounded-full p-3 shadow-lg cursor-pointer transition-all transform hover:scale-105"
                title="Logout"
            >
                {/* Futuristic Logout SVG */}
                <svg
                    viewBox="0 0 24 24"
                    className="w-6 h-6 text-white"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                >
                    <path
                        d="M9 21H5a2 2 0 01-2-2V5a2 2 0 012-2h4M16 17l5-5-5-5M21 12H9"
                        stroke="currentColor"
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                    />
                </svg>
            </div>

            {/* Add Platform Button */}
            <div
                onClick={() => setIsAddModalOpen(true)}
                className="fixed bottom-6 right-6 bg-blue-500 hover:bg-blue-600 rounded-full p-3 shadow-lg cursor-pointer transition"
            >
                <PlusCircle size={24} />
            </div>

            {/* Add Platform Modal */}
            <AddPlatformModal
                isOpen={isAddModalOpen}
                onClose={() => setIsAddModalOpen(false)}
            />
        </div>
    );
};

const Layout = ({ children }) => {
    return (
        <div className="flex">
            <Sidebar />
            <main className="ml-64 flex-grow p-6 bg-gray-100 min-h-screen">
                {children}
            </main>
        </div>
    );
};

export default Layout;
