import axios from 'axios';

class PlatformService {
    constructor() {
        // Use environment variable with fallback
        this.baseURL = process.env.REACT_APP_API_BASE_URL || '';
        this.platformsURL = `${this.baseURL}/api/platforms`;
        this.contentURL = '/api/content';
    }

    // Platform methods
    async getUserPlatforms() {
        try {
            const response = await axios.get(this.platformsURL);
            return response.data;
        } catch (error) {
            console.error('Error fetching platforms:', error);
            throw error;
        }
    }

    async addPlatform(platformData) {
        try {
            const response = await axios.post(this.platformsURL, platformData);
            return response.data;
        } catch (error) {
            console.error('Error adding platform:', error);
            throw error;
        }
    }

    // Content methods
    async addContent(contentData) {
        try {
            const response = await axios.post(this.contentURL, contentData);
            return response.data;
        } catch (error) {
            console.error('Error adding content:', error);
            throw error;
        }
    }

    async getContentByPlatform(platformId) {
        try {
            const response = await axios.get(`${this.platformsURL}/${platformId}/content`);
            return response.data;
        } catch (error) {
            console.error('Error fetching content:', error);
            throw error;
        }
    }
}

export default new PlatformService();
