import React, { useState, useEffect } from 'react';
import platformService from '../services/PlatformService';
import {
    Youtube,
    Instagram,
    Twitter,
    Facebook,
    Twitch
} from 'lucide-react';

const PlatformsPage = () => {
    const [platforms, setPlatforms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Platform icon mapping
    const platformIcons = {
        'YouTube': Youtube,
        'Instagram': Instagram,
        'Twitter': Twitter,
        'Facebook': Facebook,
        'Twitch': Twitch
    };

    useEffect(() => {
        const fetchPlatforms = async () => {
            try {
                const response = await platformService.getUserPlatforms();
                console.log("Platforms response", response);

                // Ensuring we have an array of platforms
                const userPlatforms = Array.isArray(response) ? response : [];
                setPlatforms(userPlatforms);
            } catch (error) {
                console.error('Failed to fetch platforms', error);
                setError("Failed to load platforms");
            } finally {
                setLoading(false);
            }
        };

        fetchPlatforms();
    }, []);

    // Group platforms by platform name
    const groupedPlatforms = platforms.reduce((acc, platform) => {
        const name = platform.platformName;
        if (!acc[name]) {
            acc[name] = [];
        }
        acc[name].push(platform);
        return acc;
    }, {});

    // Render platform card - one per platform type
    const renderPlatformTypeCard = (platformName, platformList) => {
        const PlatformIcon = platformIcons[platformName] || 'div';

        return (
            <div
                key={platformName}
                className="bg-white shadow-md rounded-lg p-6"
            >
                <div className="flex items-center space-x-4 mb-4">
                    <div className="bg-gray-100 p-4 rounded-full">
                        <PlatformIcon size={48} className="text-gray-600" />
                    </div>
                    <div>
                        <h3 className="text-xl font-bold">{platformName}</h3>
                        <p className="text-gray-500">{platformList.length} connected {platformList.length === 1 ? 'account' : 'accounts'}</p>
                    </div>
                </div>

                <div className="grid grid-cols-2 gap-4 mt-4">
                    <div className="bg-gray-50 p-4 rounded-lg text-center">
                        <p className="text-xl font-bold">0</p>
                        <p className="text-gray-500">Content Items</p>
                    </div>
                    <div className="bg-gray-50 p-4 rounded-lg text-center">
                        <p className="text-xl font-bold">0</p>
                        <p className="text-gray-500">Total Views</p>
                    </div>
                </div>
            </div>
        );
    };

    if (loading) {
        return <div className="text-center p-6">Loading platforms...</div>;
    }

    if (error) {
        return <div className="text-center text-red-500 p-6">{error}</div>;
    }

    return (
        <div className="container mx-auto px-4">
            <h1 className="text-3xl font-bold mb-6">My Platforms</h1>

            {Object.keys(groupedPlatforms).length === 0 ? (
                <div className="text-center text-gray-500 p-10">
                    No platforms added yet. Click the + button to get started.
                </div>
            ) : (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {Object.entries(groupedPlatforms).map(([name, list]) =>
                        renderPlatformTypeCard(name, list)
                    )}
                </div>
            )}
        </div>
    );
};

export default PlatformsPage;
