import axios from 'axios';
import api from './ApiService';

const API_URL = 'http://localhost:8080/api/auth';

class AuthService {
    async login(username, password) {
        try {
            console.log("Attempting login for user: ", username);
            const response = await axios.post(`${API_URL}/login`, {username, password});

            if (response.data && response.data.token) {
                console.log(' Login successful, token received');
                localStorage.setItem('token', response.data.token);
                console.log("Token saved")
            } else {
                console.log("no Token in response", response.data);
            }

            return response.data;
        } catch (error) {
            console.error("Login failed:", error.response?.data || error.message);
            throw error;
        }
    }

    async register(userData) {
        console.log("Sending registration data:", userData);
        try {
            const response = await axios.post(`${API_URL}/register`, userData);
            console.log("Registration response:", response.data);
            return response.data
        } catch (error) {
            console.error("Registration error", error);
            throw error;
        }
    }

    async getCurrentUser() {
        try {
            console.log("Fetching current user data");
            const token = this.getToken();

            if (!token) {
                console.log("No token found");
                throw new Error("Not authenticated");
            }

            // Use axios with the token in the header
            const response = await axios.get(`${API_URL}/me`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            console.log("User data fetched successfully");
            return response.data;
        } catch (error) {
            console.error("Failed to get current user", error.response?.data || error.message);
            throw error;
        }
    }

    getToken() {
        const token = localStorage.getItem('token');
        console.log("Retrieved token from localStorage:", token ? "Present" : "Not found");
        return token;    }

    logout() {
        console.log("Logging out, removing token");
        localStorage.removeItem('token');
    }
}

export default new AuthService();
