import React, { useState, useEffect } from 'react';
import api from '../../services/ApiService';
import metricsService from '../../services/MetricsService';
import {
    LineChart, Line, BarChart, Bar, PieChart, Pie, Cell,
    XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

const DashboardPage = () => {
    const [dashboardData, setDashboardData] = useState(null);
    const [metricsData, setMetricsData] = useState(null);
    const [timeSeriesData, setTimeSeriesData] = useState([]);
    const [platformMetrics, setPlatformMetrics] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Define colors for charts
    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);

                // Fetch all data in parallel
                const [dashboardResponse, metricsResponse, timeSeriesResponse, platformResponse] =
                    await Promise.all([
                        api.get('/dashboard/summary'),
                        metricsService.getMetricsSummary(),
                        metricsService.getTimeSeriesData(),
                        metricsService.getMetricsByPlatform()
                    ]);

                console.log("Dashboard data:", dashboardResponse.data);
                console.log("Metrics data:", metricsResponse);
                console.log("Raw time series data:", timeSeriesResponse);
                console.log("Platform metrics:", platformResponse);

                // Process time series data to ensure it's valid for charting
                let processedTimeSeriesData = [];

                // Check if we have valid data
                if (timeSeriesResponse && Array.isArray(timeSeriesResponse)) {
                    // Handle nested array case
                    if (timeSeriesResponse.length === 1 && Array.isArray(timeSeriesResponse[0])) {
                        // Try to use the nested array if it's not just [null]
                        if (timeSeriesResponse[0].some(item => item !== null)) {
                            processedTimeSeriesData = timeSeriesResponse[0].filter(item => item !== null);
                        }
                    } else if (timeSeriesResponse.length > 0) {
                        // Direct array of data
                        processedTimeSeriesData = timeSeriesResponse.filter(item => item !== null);
                    }
                }

                // If no valid data, generate sample data
                if (processedTimeSeriesData.length === 0) {
                    const now = new Date();
                    const sampleData = [];

                    // Generate 30 days of sample data
                    for (let i = 29; i >= 0; i--) {
                        const date = new Date(now);
                        date.setDate(date.getDate() - i);
                        const dateStr = date.toISOString().split('T')[0];

                        // Start with larger values and increase
                        const views = 1000000 + (Math.random() * 100000 * (30-i));
                        const likes = 100000 + (Math.random() * 10000 * (30-i));
                        const comments = 5000 + (Math.random() * 500 * (30-i));

                        sampleData.push({
                            date: dateStr,
                            views: Math.round(views),
                            likes: Math.round(likes),
                            comments: Math.round(comments)
                        });
                    }

                    processedTimeSeriesData = sampleData;
                }

                console.log("Processed time series data:", processedTimeSeriesData);

                setDashboardData(dashboardResponse.data);
                setMetricsData(metricsResponse);
                setTimeSeriesData(processedTimeSeriesData);
                setPlatformMetrics(platformResponse);
            } catch (err) {
                console.error("Error loading dashboard:", err);
                setError('Failed to load dashboard data');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) return <div className="text-center p-10">Loading dashboard...</div>;
    if (error) return <div className="text-center p-10 text-red-500">{error}</div>;
    if (!dashboardData) return <div className="text-center p-10">No dashboard data available</div>;

    // Derive data for platform distribution chart
    const platformData = (() => {
        try {
            const distribution = dashboardData.platforms?.platformDistribution || {};
            const formattedData = Object.entries(distribution).map(
                ([name, value]) => ({ name, value: Number(value) })
            );
            return formattedData.length > 0 ? formattedData : [{ name: 'No Platforms', value: 1 }];
        } catch (err) {
            console.error("Error formatting platform data:", err);
            return [{ name: 'No Platforms', value: 1 }];
        }
    })();

    // Derive data for content type distribution
    const contentTypeData = (() => {
        try {
            const distribution = dashboardData.content?.contentTypeDistribution || {};
            const formattedData = Object.entries(distribution).map(
                ([name, value]) => ({
                    name: name.replace(/_/g, ' ').toLowerCase()
                        .split(' ')
                        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
                        .join(' '),
                    value: Number(value)
                })
            );
            return formattedData.length > 0 ? formattedData : [{ name: 'No Content', value: 1 }];
        } catch (err) {
            console.error("Error formatting content type data:", err);
            return [{ name: 'No Content', value: 1 }];
        }
    })();

    return (
        <div className="container mx-auto">
            <h1 className="text-3xl font-bold mb-6">Analytics Dashboard</h1>

            {/* Stats Cards */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Platforms</h2>
                    <p className="text-3xl font-bold">{dashboardData?.platforms?.totalPlatforms || 0}</p>
                    <p className="text-gray-500">Connected platforms</p>
                </div>

                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Content</h2>
                    <p className="text-3xl font-bold">{dashboardData?.content?.totalContents || 0}</p>
                    <p className="text-gray-500">Total content pieces</p>
                </div>

                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Views</h2>
                    <p className="text-3xl font-bold">{metricsData?.totalViews || 0}</p>
                    <p className="text-gray-500">Total views</p>
                </div>

                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Engagement</h2>
                    <p className="text-3xl font-bold">{(metricsData?.averageEngagementRate || 0).toFixed(2)}%</p>
                    <p className="text-gray-500">Average engagement rate</p>
                </div>
            </div>

            {/* Engagement Over Time Chart */}
            <div className="bg-white rounded-lg shadow p-6 mb-8">
                <h2 className="text-xl font-semibold mb-4">Engagement Over Time</h2>
                <ResponsiveContainer width="100%" height={300}>
                    <LineChart data={timeSeriesData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="date" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Line type="monotone" dataKey="views" stroke="#8884d8" name="Views" />
                        <Line type="monotone" dataKey="likes" stroke="#82ca9d" name="Likes" />
                        <Line type="monotone" dataKey="comments" stroke="#ffc658" name="Comments" />
                    </LineChart>
                </ResponsiveContainer>
            </div>

            {/* Charts Row */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                {/* Platform Distribution */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-4">Platform Distribution</h2>
                    <ResponsiveContainer width="100%" height={300}>
                        <PieChart>
                            <Pie
                                data={platformData}
                                cx="50%"
                                cy="50%"
                                labelLine={false}
                                outerRadius={100}
                                fill="#8884d8"
                                dataKey="value"
                                label={({ name, percent }) =>
                                    platformData[0].name === 'No Platforms'
                                        ? 'No platforms connected yet'
                                        :`${name} ${(percent * 100).toFixed(0)}%`}
                            >
                                {platformData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                ))}
                            </Pie>
                            <Tooltip />
                        </PieChart>
                    </ResponsiveContainer>
                </div>

                {/* Platform Performance */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-4">Platform Performance</h2>
                    {platformMetrics.length > 0 ? (
                        <ResponsiveContainer width="100%" height={300}>
                            <BarChart data={platformMetrics}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="platform" />
                                <YAxis />
                                <Tooltip />
                                <Legend />
                                <Bar dataKey="engagementRate" fill="#82ca9d" name="Engagement Rate %" />
                            </BarChart>
                        </ResponsiveContainer>
                    ) : (
                        <p className="text-gray-500 text-center">No platform performance data available</p>
                    )}
                </div>
            </div>

            {/* Content Type Distribution */}
            <div className="bg-white rounded-lg shadow p-6 mb-8">
                <h2 className="text-xl font-semibold mb-4">Content Type Distribution</h2>
                <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={contentTypeData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Bar
                            dataKey="value"
                            fill="#8884d8"
                            name="Content Count"
                            label={
                                contentTypeData[0].name === 'No Content'
                                    ? { value: 'No content added yet', position: 'center' }
                                    : { position: 'top' }
                            }
                        />
                    </BarChart>
                </ResponsiveContainer>
            </div>

            {/* Recent Content Section */}
            <div className="bg-white rounded-lg shadow p-6">
                <h2 className="text-xl font-semibold mb-4">Recent Content</h2>
                {dashboardData?.content?.recentContent?.length > 0 ? (
                    <table className="min-w-full">
                        <thead>
                        <tr className="bg-gray-100">
                            <th className="py-2 px-4 text-left">Title</th>
                            <th className="py-2 px-4 text-left">Platform</th>
                            <th className="py-2 px-4 text-left">Type</th>
                            <th className="py-2 px-4 text-left">Published</th>
                            <th className="py-2 px-4 text-left">Views</th>
                            <th className="py-2 px-4 text-left">Actions</th>
                        </tr>
                        </thead>
                        <tbody>
                        {dashboardData.content.recentContent.map((content, index) => (
                            <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : ''}>
                                <td className="py-2 px-4">{content.title}</td>
                                <td className="py-2 px-4">
                                    {typeof content.platform === 'object' && content.platform !== null
                                        ? content.platform.platformName
                                        : typeof content.platform === 'number'
                                            ? `Platform ID: ${content.platform}`
                                            : 'Unknown Platform'}
                                </td>
                                <td className="py-2 px-4">{content.contentType}</td>
                                <td className="py-2 px-4">
                                    {content.publishedDate
                                        ? new Date(content.publishedDate).toLocaleDateString()
                                        : 'Not Published'}
                                </td>
                                <td className="py-2 px-4">-</td>
                                <td className="py-2 px-4">
                                    <button
                                        onClick={() => metricsService.refreshContentMetrics(content.id)}
                                        className="text-xs bg-blue-500 hover:bg-blue-600 text-white px-2 py-1 rounded"
                                    >
                                        Refresh Metrics
                                    </button>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                ) : (
                    <p className="text-gray-500">No recent content available</p>
                )}
            </div>
        </div>
    );
};

export default DashboardPage;
