import React, { useState, useEffect } from 'react';
import platformService from '../services/PlatformService';
import {
    Youtube,
    Instagram,
    Twitter,
    Facebook,
    Twitch
} from 'lucide-react';

const PlatformsPage = () => {
    const [platforms, setPlatforms] = useState([]);
    const [loading, setLoading] = useState(true);

    // Platform icon mapping
    const platformIcons = {
        'YouTube': Youtube,
        'Instagram': Instagram,
        'Twitter': Twitter,
        'Facebook': Facebook,
        'Twitch': Twitch
    };

    useEffect(() => {
        const fetchPlatforms = async () => {
            try {
                const userPlatforms = await platformService.getUserPlatforms();
                setPlatforms(userPlatforms);
                setLoading(false);
            } catch (error) {
                console.error('Failed to fetch platforms', error);
                setLoading(false);
            }
        };

        fetchPlatforms();
    }, []);

    // Render platform card
    const renderPlatformCard = (platform) => {
        const PlatformIcon = platformIcons[platform.name] || 'div';

        return (
            <div
                key={platform.id}
                className="bg-white shadow-md rounded-lg p-6 flex items-center space-x-4"
            >
                <PlatformIcon size={48} className="text-gray-600" />
                <div>
                    <h3 className="text-xl font-semibold">{platform.name}</h3>
                    <p className="text-gray-500 truncate max-w-xs">{platform.url}</p>
                </div>
            </div>
        );
    };

    if (loading) {
        return <div>Loading platforms...</div>;
    }

    return (
        <div className="container mx-auto px-4">
            <h1 className="text-3xl font-bold mb-6">My Platforms</h1>

            {platforms.length === 0 ? (
                <div className="text-center text-gray-500">
                    No platforms added yet. Click the + button to get started.
                </div>
            ) : (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {platforms.map(renderPlatformCard)}
                </div>
            )}
        </div>
    );
};

export default PlatformsPage;
