import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import React, { useState, useEffect } from 'react';
import platformService from '../services/PlatformService';
import metricsService from '../services/MetricsService';
import PlatformMetricsModal from '../components/Platforms/PlatformMetricsModal';
import {
    Youtube,
    Instagram,
    Twitter,
    Facebook,
    Twitch,
    BarChart,
    Eye,
    ChevronRight
} from 'lucide-react';

const PlatformsPage = () => {
    const [platforms, setPlatforms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedPlatform, setSelectedPlatform] = useState(null);
    const [selectedContent, setSelectedContent] = useState(null);
    const [showMetricsModal, setShowMetricsModal] = useState(false);
    const [showContentMetricsModal, setShowContentMetricsModal] = useState(false);
    const [contentByPlatform, setContentByPlatform] = useState({});
    const [groupedPlatforms, setGroupedPlatforms] = useState({});

    // Platform icon mapping
    const platformIcons = {
        'YouTube': Youtube,
        'Instagram': Instagram,
        'Twitter': Twitter,
        'Facebook': Facebook,
        'Twitch': Twitch
    };

    useEffect(() => {
        const fetchPlatforms = async () => {
            try {
                const userPlatforms = await platformService.getUserPlatforms();
                setPlatforms(userPlatforms);

                // Group platforms by platform name
                const grouped = {};
                userPlatforms.forEach(platform => {
                    if (!grouped[platform.platformName]) {
                        grouped[platform.platformName] = {
                            ...platform,
                            accounts: [platform]
                        };
                    } else {
                        grouped[platform.platformName].accounts.push(platform);
                    }
                });

                setGroupedPlatforms(grouped);

                // Fetch content for each platform
                const contentMap = {};
                for (const platform of userPlatforms) {
                    try {
                        const contentList = await metricsService.getPlatformContentList(platform.id);

                        // Group content by type
                        const groupedContent = {};
                        contentList.forEach(content => {
                            const type = content.contentType || 'Other';
                            if (!groupedContent[type]) {
                                groupedContent[type] = [];
                            }
                            groupedContent[type].push(content);
                        });

                        contentMap[platform.id] = groupedContent;
                    } catch (error) {
                        console.error(`Failed to fetch content for platform ${platform.id}:`, error);
                        contentMap[platform.id] = {};
                    }
                }
                setContentByPlatform(contentMap);

                setLoading(false);
            } catch (error) {
                console.error('Failed to fetch platforms', error);
                setLoading(false);
            }
        };

        fetchPlatforms();
    }, []);

    const handlePlatformClick = (platform) => {
        setSelectedPlatform(platform);
        setShowMetricsModal(true);
    };

    const handleContentClick = (content) => {
        setSelectedContent(content);
        setShowContentMetricsModal(true);
    };

    const closeMetricsModal = () => {
        setShowMetricsModal(false);
        setSelectedPlatform(null);
    };

    const closeContentMetricsModal = () => {
        setShowContentMetricsModal(false);
        setSelectedContent(null);
    };

    // Render consolidated platform card
    const renderConsolidatedPlatformCard = (platformName, platformData) => {
        const PlatformIcon = platformIcons[platformName] || 'div';

        // Get all content from all accounts of this platform type
        const allContent = {};
        platformData.accounts.forEach(platform => {
            const platformContent = contentByPlatform[platform.id] || {};

            // Merge content by type
            Object.keys(platformContent).forEach(type => {
                if (!allContent[type]) {
                    allContent[type] = [];
                }
                allContent[type] = [...allContent[type], ...platformContent[type]];
            });
        });

        return (
            <div
                key={platformName}
                className="bg-white shadow-md rounded-lg p-6 flex flex-col mb-6"
            >
                <div className="flex items-center space-x-4 mb-4">
                    <PlatformIcon size={48} className="text-gray-600" />
                    <div>
                        <h3 className="text-xl font-semibold">{platformName}</h3>
                        <p className="text-gray-500">
                            {platformData.accounts.length} connected {platformData.accounts.length > 1 ? 'accounts' : 'account'}
                        </p>
                    </div>
                </div>

                <div className="text-sm text-gray-500 mb-3">
                    Connected: {new Date(platformData.createdAt).toLocaleDateString()}
                </div>

                {/* Content Types Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-2">
                    {Object.keys(allContent).length > 0 ? (
                        Object.keys(allContent).map(contentType => (
                            <div key={contentType} className="border rounded-md p-2">
                                <h4 className="text-sm font-medium mb-2">{contentType}</h4>
                                <div className="max-h-40 overflow-y-auto pr-1">
                                    {allContent[contentType].map(content => (
                                        <div
                                            key={content.id}
                                            className="flex justify-between items-center py-1 border-b border-gray-100 hover:bg-gray-50 cursor-pointer"
                                            onClick={() => handleContentClick(content)}
                                        >
                                            <span className="text-xs text-gray-700 truncate" style={{maxWidth: "180px"}}>
                                                {content.title}
                                            </span>
                                            <div className="flex items-center">
                                                <span className="text-xs text-gray-500 flex items-center mr-2 whitespace-nowrap">
                                                    <Eye size={12} className="mr-1" />
                                                    {content.views?.toLocaleString() || 0}
                                                </span>
                                                <ChevronRight size={12} className="text-gray-400" />
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))
                    ) : (
                        <div className="text-xs text-gray-400 text-center py-2 col-span-2">
                            No content available
                        </div>
                    )}
                </div>

                <div className="mt-auto pt-4 border-t border-gray-200 flex justify-end">
                    <button
                        onClick={() => handlePlatformClick(platformData.accounts[0])}
                        className="flex items-center space-x-1 text-blue-500 hover:text-blue-700"
                    >
                        <BarChart size={16} />
                        <span>View Platform Metrics</span>
                    </button>
                </div>
            </div>
        );
    };

    if (loading) {
        return (
            <div className="flex justify-center items-center h-64">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
            </div>
        );
    }

    return (
        <div className="container mx-auto px-4">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold">My Platforms</h1>
            </div>

            {Object.keys(groupedPlatforms).length === 0 ? (
                <div className="text-center py-12 bg-gray-50 rounded-lg">
                    <h3 className="text-lg font-medium text-gray-500 mb-2">No platforms added yet</h3>
                    <p className="text-gray-400">
                        Click the + button in the bottom right to add your first platform
                    </p>
                </div>
            ) : (
                <div className="space-y-6">
                    {Object.entries(groupedPlatforms).map(([platformName, platformData]) =>
                        renderConsolidatedPlatformCard(platformName, platformData)
                    )}
                </div>
            )}

            {/* Platform Metrics Modal */}
            {selectedPlatform && (
                <PlatformMetricsModal
                    platform={selectedPlatform}
                    isOpen={showMetricsModal}
                    onClose={closeMetricsModal}
                />
            )}

            {/* Content Metrics Modal - You'll need to create this component */}
            {selectedContent && showContentMetricsModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
                    <div className="bg-white rounded-lg shadow-xl w-11/12 max-w-4xl max-h-90vh overflow-y-auto p-6">
                        <div className="flex justify-between items-center mb-4">
                            <h2 className="text-2xl font-bold">Content Analytics: {selectedContent.title}</h2>
                            <button
                                onClick={closeContentMetricsModal}
                                className="p-2 rounded-full hover:bg-gray-200 transition-colors"
                            >
                                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><line x1="18" y1="6" x2="6" y2="18"></line><line x1="6" y1="6" x2="18" y2="18"></line></svg>
                            </button>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                            <div className="bg-gray-50 p-4 rounded shadow">
                                <h3 className="text-sm font-medium text-gray-500">Views</h3>
                                <p className="text-2xl font-bold">{selectedContent.views?.toLocaleString() || 0}</p>
                            </div>
                            <div className="bg-gray-50 p-4 rounded shadow">
                                <h3 className="text-sm font-medium text-gray-500">Likes</h3>
                                <p className="text-2xl font-bold">{selectedContent.likes?.toLocaleString() || 0}</p>
                            </div>
                            <div className="bg-gray-50 p-4 rounded shadow">
                                <h3 className="text-sm font-medium text-gray-500">Engagement Rate</h3>
                                <p className="text-2xl font-bold">{selectedContent.engagementRate?.toFixed(1) || 0}%</p>
                            </div>
                        </div>

                        <div className="text-center py-8">
                            <p className="text-gray-500">Detailed analytics for this content item will be available soon.</p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default PlatformsPage;
