import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
    PieChart, Pie, Cell,
} from 'recharts';

const DashboardPage = () => {
    const [dashboardData, setDashboardData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

    useEffect(() => {
        const fetchDashboardData = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/dashboard/summary');
                console.log("FULL API RESPONSE:", JSON.stringify(response.data, null, 2));
                setDashboardData(response.data);
                setLoading(false);
            } catch(err) {
                console.error("Dashboard error:", err);
                setError('Failed to load dashboard data');
                setLoading(false);
            }
        };

        fetchDashboardData();
    }, []);

    if (loading) return <div className="text-center p-10">Loading dashboard...</div>;
    if (error) return <div className="text-center p-10 text-red-500">{error}</div>;
    if (!dashboardData) return <div className="text-center p-10">No dashboard data available</div>;

    // Add safety checks for all data access
    const totalPlatform = dashboardData.platforms?.totalPlatforms || 0;
    const totalContent = dashboardData.content?.totalContents || 0;

    const platformData = (() => {
        try {
            // Extract platform distribution data
            const distribution = dashboardData.platforms?.platformDistribution || {};

            // Convert to array format needed by charts
            const formattedData = Object.entries(distribution).map(
                ([name, value]) => ({ name, value: Number(value) })
            );

            // Add placeholder if empty
            return formattedData.length > 0
                ? formattedData
                : [{ name: 'No Platforms', value: 1 }];
        } catch (err) {
            console.error("Error formatting platform data:", err);
            return [{ name: 'No Platforms', value: 1 }];
        }
    })();

    const contentTypeData = (() => {
        try {
            // Extract content type distribution
            const distribution = dashboardData.content?.contentTypeDistribution || {};

            // Convert to array format needed by charts
            const formattedData = Object.entries(distribution).map(
                ([name, value]) => ({
                    name: name.replace(/_/g, ' ').toLowerCase()
                        .split(' ')
                        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
                        .join(' '),
                    value: Number(value)
                })
            );

            // Add placeholder if empty
            return formattedData.length > 0
                ? formattedData
                : [{ name: 'No Content', value: 1 }];
        } catch (err) {
            console.error("Error formatting content type data:", err);
            return [{ name: 'No Content', value: 1 }];
        }
    })();


    return (
        <div className="container mx-auto">
            <h1 className="text-3xl font-bold mb-6">Analytics Dashboard</h1>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                {/* Platform Stats Card */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Platforms</h2>
                    <p className="text-3xl font-bold">{dashboardData.platforms?.totalPlatforms || 0}</p>
                    <p className="text-gray-500">Connected platforms</p>
                </div>

                {/* Content Stats Card */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Content</h2>
                    <p className="text-3xl font-bold">{dashboardData.content?.totalContents || 0}</p>
                    <p className="text-gray-500">Total content pieces</p>
                </div>

                {/* Engagement Stats Card */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Engagement</h2>
                    <p className="text-3xl font-bold">{dashboardData.metrics?.averageEngagementRate?.toFixed(2) || '0.00'}%</p>
                    <p className="text-gray-500">Average engagement rate</p>
                </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                {/* Platform Distribution Chart */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-4">Platform Distribution</h2>
                    <ResponsiveContainer width="100%" height={300}>
                        <PieChart>
                            <Pie
                                data={platformData}
                                cx="50%"
                                cy="50%"
                                labelLine={platformData[0].name !== 'No Platforms'}
                                outerRadius={100}
                                fill="#8884d8"
                                dataKey="value"
                                label={({ name, percent }) =>
                                    platformData[0].name === 'No Platforms'
                                        ? 'No platforms connected yet'
                                        :`${name} ${(percent * 100).toFixed(0)}%`}
                            >
                                {platformData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                ))}
                            </Pie>
                            <Tooltip />
                        </PieChart>
                    </ResponsiveContainer>
                </div>

                {/* Content Type Distribution Chart */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-4">Content Type Distribution</h2>
                    <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={contentTypeData}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar
                                dataKey="value"
                                fill="#8884d8"
                                name = "Content Count"
                                label = {
                                    contentTypeData[0].name === 'No Content'
                                        ? { value: 'No content added yet', position: 'center' }
                                        : { position: 'top' }
                                }
                            />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            </div>

            {/* Recent Content Section */}
            <div className="bg-white rounded-lg shadow p-6">
                <h2 className="text-xl font-semibold mb-4">Recent Content</h2>
                {dashboardData?.content?.recentContent?.length > 0 ? (
                    <table className="min-w-full">
                        <thead>
                        <tr className="bg-gray-100">
                            <th className="py-2 px-4 text-left">Title</th>
                            <th className="py-2 px-4 text-left">Platform</th>
                            <th className="py-2 px-4 text-left">Type</th>
                            <th className="py-2 px-4 text-left">Published</th>
                            <th className="py-2 px-4 text-left">Views</th>
                        </tr>
                        </thead>
                        <tbody>
                        {dashboardData.content.recentContent.map((content, index) => (
                            <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : ''}>
                                <td className="py-2 px-4">{content.title}</td>
                                <td className="py-2 px-4">{content.platform.platformName}</td>
                                <td className="py-2 px-4">{content.contentType}</td>
                                <td className="py-2 px-4">
                                    {new Date(content.publishedDate).toLocaleDateString()}
                                </td>
                                <td className="py-2 px-4">-</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                ) : (
                    <p className="text-gray-500">No recent content available</p>
                )}
            </div>
        </div>
    );
};

export default DashboardPage;
