import React, { useState, useEffect } from 'react';
import platformService from '../services/PlatformService';
import metricsService from '../services/MetricsService';
import PlatformMetricsModal from '../components/Platforms/PlatformMetricsModal';
import {
    Youtube,
    Instagram,
    Twitter,
    Facebook,
    Twitch,
    BarChart
} from 'lucide-react';

const PlatformsPage = () => {
    const [platforms, setPlatforms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedPlatform, setSelectedPlatform] = useState(null);
    const [showMetricsModal, setShowMetricsModal] = useState(false);
    const [contentByPlatform, setContentByPlatform] = useState({});

    // Platform icon mapping
    const platformIcons = {
        'YouTube': Youtube,
        'Instagram': Instagram,
        'Twitter': Twitter,
        'Facebook': Facebook,
        'Twitch': Twitch
    };

    useEffect(() => {
        const fetchPlatforms = async () => {
            try {
                const userPlatforms = await platformService.getUserPlatforms();
                setPlatforms(userPlatforms);

                // Fetch content for each platform
                const contentMap = {};
                for (const platform of userPlatforms) {
                    try {
                        const contentList = await metricsService.getPlatformContentList(platform.id);
                        contentMap[platform.id] = contentList;
                    } catch (error) {
                        console.error(`Failed to fetch content for platform ${platform.id}:`, error);
                        contentMap[platform.id] = [];
                    }
                }
                setContentByPlatform(contentMap);

                setLoading(false);
            } catch (error) {
                console.error('Failed to fetch platforms', error);
                setLoading(false);
            }
        };

        fetchPlatforms();
    }, []);

    const handlePlatformClick = (platform) => {
        setSelectedPlatform(platform);
        setShowMetricsModal(true);
    };

    const closeMetricsModal = () => {
        setShowMetricsModal(false);
        setSelectedPlatform(null);
    };

    // Render platform card
    const renderPlatformCard = (platform) => {
        const PlatformIcon = platformIcons[platform.platformName] || 'div';
        const platformContent = contentByPlatform[platform.id] || [];

        return (
            <div
                key={platform.id}
                className="bg-white shadow-md rounded-lg p-6 flex flex-col"
            >
                <div className="flex items-center space-x-4 mb-4">
                    <PlatformIcon size={48} className="text-gray-600" />
                    <div>
                        <h3 className="text-xl font-semibold">{platform.platformName}</h3>
                        <p className="text-gray-500 truncate max-w-xs">{platform.platformUsername || platform.platformName + ' User'}</p>
                    </div>
                </div>

                <div className="mt-auto">
                    <div className="text-sm text-gray-500 mb-2">
                        Connected: {new Date(platform.createdAt).toLocaleDateString()}
                    </div>

                    {/* Scrollable Content List */}
                    <div className="mt-2 border border-gray-200 rounded-md max-h-40 overflow-y-auto">
                        {platformContent.length > 0 ? (
                            <table className="min-w-full divide-y divide-gray-200">
                                <thead className="bg-gray-50 sticky top-0">
                                <tr>
                                    <th className="px-3 py-2 text-xs font-medium text-gray-500 text-left">Content</th>
                                    <th className="px-3 py-2 text-xs font-medium text-gray-500 text-right">Views</th>
                                    <th className="px-3 py-2 text-xs font-medium text-gray-500 text-right">Likes</th>
                                </tr>
                                </thead>
                                <tbody className="bg-white divide-y divide-gray-200">
                                {platformContent.map(content => (
                                    <tr key={content.id} className="hover:bg-gray-50">
                                        <td className="px-3 py-2 text-xs text-gray-600 whitespace-nowrap overflow-hidden text-ellipsis" style={{maxWidth: "150px"}}>
                                            {content.title}
                                        </td>
                                        <td className="px-3 py-2 text-xs text-gray-600 text-right">
                                            {content.views?.toLocaleString() || 0}
                                        </td>
                                        <td className="px-3 py-2 text-xs text-gray-600 text-right">
                                            {content.likes?.toLocaleString() || 0}
                                        </td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        ) : (
                            <div className="p-3 text-sm text-gray-400 text-center">
                                No content available
                            </div>
                        )}
                    </div>

                    <div className="pt-4 border-t border-gray-200 mt-2 flex justify-end">
                        <button
                            onClick={() => handlePlatformClick(platform)}
                            className="flex items-center space-x-1 text-blue-500 hover:text-blue-700"
                        >
                            <BarChart size={16} />
                            <span>View Metrics</span>
                        </button>
                    </div>
                </div>
            </div>
        );
    };

    if (loading) {
        return (
            <div className="flex justify-center items-center h-64">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
            </div>
        );
    }

    return (
        <div className="container mx-auto px-4">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold">My Platforms</h1>
            </div>

            {platforms.length === 0 ? (
                <div className="text-center py-12 bg-gray-50 rounded-lg">
                    <h3 className="text-lg font-medium text-gray-500 mb-2">No platforms added yet</h3>
                    <p className="text-gray-400">
                        Click the + button in the bottom right to add your first platform
                    </p>
                </div>
            ) : (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {platforms.map(renderPlatformCard)}
                </div>
            )}

            {/* Platform Metrics Modal */}
            {selectedPlatform && (
                <PlatformMetricsModal
                    platform={selectedPlatform}
                    isOpen={showMetricsModal}
                    onClose={closeMetricsModal}
                />
            )}
        </div>
    );
};

export default PlatformsPage;
