import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    BarChart, Bar, XAxis, YAxis, CartesianGrid, ToolTip, Legend, ResponsiveContainer,
    PieChart, Pie, Cell,
} from 'recharts';

const DashboardPage = () => {
    const [dashboardData, setDashboardData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('api/dashboard/summary');
                setDashboardData(response.data);
                setLoading(false);
            } catch(err) {
                setError('Failed to load dashboard data');
                setLoading(false);
            }
        };

        fetchDashboardData();
    }, []);

    if (loading) return <div className="text-center p-10">Loading dashboard...</div>;
    if (error) return <div className="text-center p-10 text-red-500">{error}</div>;
    if (!dashboardData) return <div className="text-center p-10">No data available</div>;

    // Formatting platform distribution data for charts
    const platformData = Object.entries(dashboardData.platforms.platformDistribution || {}).map(
        ([name, value]) => ({name, value})
    );

    // Formatting content type distribution
    const contentTypeData = Object.entries(dashboardData.content.contentTypeDistribution || {}).map(
        ([name, value]) => ({name, value})
    );

    return (
        <div className="container mx-auto">
            <h1 className="text-3xl font-bold mb-6">Analytics Dashboard</h1>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                {/* Platform Stats Card */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Platforms</h2>
                    <p className="text-3xl font-bold">{dashboardData.platforms.totalPlatforms}</p>
                    <p className="text-gray-500">Connected platforms</p>
                </div>

                {/* Content Stats Card */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Content</h2>
                    <p className="text-3xl font-bold">{dashboardData.content.totalContent}</p>
                    <p className="text-gray-500">Total content pieces</p>
                </div>

                {/* Engagement Stats Card */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-2">Engagement</h2>
                    <p className="text-3xl font-bold">{dashboardData.metrics.averageEngagementRate?.toFixed(2)}%</p>
                    <p className="text-gray-500">Average engagement rate</p>
                </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                {/* Platform Distribution Chart */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-4">Platform Distribution</h2>
                    <ResponsiveContainer width="100%" height={300}>
                        <PieChart>
                            <Pie
                                data={platformData}
                                cx="50%"
                                cy="50%"
                                labelLine={false}
                                outerRadius={100}
                                fill="#8884d8"
                                dataKey="value"
                                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                            >
                                {platformData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                ))}
                            </Pie>
                            <Tooltip />
                        </PieChart>
                    </ResponsiveContainer>
                </div>

                {/* Content Type Distribution Chart */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-4">Content Type Distribution</h2>
                    <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={contentTypeData}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="value" fill="#8884d8" />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            </div>

            {/* Recent Content Section */}
            <div className="bg-white rounded-lg shadow p-6">
                <h2 className="text-xl font-semibold mb-4">Recent Content</h2>
                {dashboardData.content.recentContent?.length > 0 ? (
                    <table className="min-w-full">
                        <thead>
                        <tr className="bg-gray-100">
                            <th className="py-2 px-4 text-left">Title</th>
                            <th className="py-2 px-4 text-left">Platform</th>
                            <th className="py-2 px-4 text-left">Type</th>
                            <th className="py-2 px-4 text-left">Published</th>
                            <th className="py-2 px-4 text-left">Views</th>
                        </tr>
                        </thead>
                        <tbody>
                        {dashboardData.content.recentContent.map((content, index) => (
                            <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : ''}>
                                <td className="py-2 px-4">{content.title}</td>
                                <td className="py-2 px-4">{content.platform.platformName}</td>
                                <td className="py-2 px-4">{content.contentType}</td>
                                <td className="py-2 px-4">
                                    {new Date(content.publishedDate).toLocaleDateString()}
                                </td>
                                <td className="py-2 px-4">-</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                ) : (
                    <p className="text-gray-500">No recent content available</p>
                )}
            </div>
        </div>
    );
};

export default DashboardPage;
